            case 0x99: do_sbb(c);            break; // SBB C
            case 0x9A: do_sbb(d);            break; // SBB D
            case 0x9B: do_sbb(e);            break; // SBB E
            case 0x9C: do_sbb(h);            break; // SBB H
            case 0x9D: do_sbb(l);            break; // SBB L
            case 0x9E: do_sbb(read_mem(hl)); break; // SBB M
            case 0x9F: do_sbb(a);            break; // SBB A

            case 0xA0: do_ana(b);               break; // ANA B
            case 0xA1: do_ana(c);               break; // ANA C
            case 0xA2: do_ana(d);               break; // ANA D
            case 0xA3: do_ana(e);               break; // ANA E
            case 0xA4: do_ana(h);               break; // ANA H
            case 0xA5: do_ana(l);               break; // ANA L
            case 0xA6: do_ana(read_mem(hl));    break; // ANA M
            case 0xA7: do_ana(a);               break; // ANA A
            case 0xA8: a ^= b; set_szp(a); ac = false; cy = false; break; // XRA B
